import './Scorecard.css'
import '../css/teams.css'
import React, { useState, useEffect } from 'react'; 

function Scorecard(props) {
    // let url = "http://localhost:8000/match/" + props.m_id + props.inn_no + "/score"
    // console.log(url)
    const [data, setData] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
        const response = await fetch(`http://localhost:8000/match/${props.m_id}/${props.inn_no}/scorecard`);
        const jsonData = await response.json();
        setData(jsonData);
        };

        fetchData();
    }, []);

    console.log(data)
    const color = data.header.color
    let class1 = "match_scorecard " + "wt20"
    let class2 = "match_scorecard_header1 " + color + "1"
    let class3 = "match_scorecard_header1 " + color + "2"
    console.log(class3)
    if (!data) {
        return <div>Loading...Scorecard</div>;
      }
    return (
        <div className={class1}>
            <div id="match_scorecard_header">
                <div className={class2}>

                </div>
                <div className={class3}>

                </div>

            </div>
            <div id="match_scorecard_body">

            </div>
        </div>
        );

    }
export default Scorecard;
